INSERT INTO `album_fotos` (`id`, `fecha`, `nombre`, `descripcion`, `publicar`) VALUES
(1, '20180227', 'Entrevista a Felipe Ruano', 'Entrevista a Felipe Ruano, Presidente de ASEMAC', 'SI'),
(2, '20170611', 'Entrevista a Alberto López-Asenjo', 'Entrevista a Alberto López-Asenjo, Secretario General de Pesca del MAPAMA', 'SI'),
(3, '20170905', 'Entrevista Alejandro Blanco ', 'Entrevista Alejandro Blanco - Presidente del COE', 'SI'),
(4, '20170120', 'Entrevista Jose Miguel Herrero ', 'Entrevista Jose Miguel Herrero - Director de AICA', 'SI'),
(5, '20171227', 'Entrevista Manuel Lainez - Director de INIA', 'Manuel Lainez, Director del Instituto Nacional de Investigacio?n y Tecnologi?a Agraria y Alimentaria (INIA) (izqda), junto a Fernando Marti?nez, Director General del Grupo Eypasa.', 'SI'),
(6, '20170607', 'III Encuentro \'Éxitos en el Mercado Internacional\'', 'III Encuentro \'Éxitos en el Mercado Internacional\'', 'SI'),
(7, '20160607', 'II Encuentro \'Éxitos en el Mercado Internacional\'', 'II Encuentro \'Éxitos en el Mercado Internacional\'', 'SI'),
(8, '20160520', 'Entrevista Aurelio del Pino, Presidente de ACES', 'Entrevista Aurelio del Pino, Presidente de ACES', 'SI'),
(9, '20160521', 'Entrevista Javier Garat, Secretario General de CEPESCA', 'CEPESCA, la Confederación Española de Pesca, nace en junio de 2007. Defiende los intereses económicos y profesionales del sector pesquero espa?ol.', 'SI'),
(10, '20160505', 'Visita Doña Letizia CIAL', 'Instituto de Investigación en Ciencias de la Alimentación. El CIAL es un Instituto mixto perteneciente al Consejo Superior de Investigaciones Científicas (CSIC) y la Universidad Autónoma de Madrid (UAM) encargado del desarrollo de la investigación científica de calidad en el área de Ciencia y Tecnología de Alimentos. La Reina Letizia le hizo una visita.\r\n', 'SI'),
(11, '20160530', 'Entrevista ASEDAS', 'La Asociación Española de Distribuidores, Autoservicios y Supermercados es la primera organización empresarial de distribución alimentaria de España. Fundada a finales de los años 90, ASEDAS ', 'SI'),
(12, '20160509', 'Entrevista Anfabra', 'La Asociación es la interlocutora ante los medios de comunicación de todo lo relacionado al sector de las bebidas refrescantes que son, bebidas analcohólicas, carbonatadas o no, elaboradas con agua de consumo humano, aguas preparadas, agua mineral natural o de manantial (en lo sucesivo agua), que contienen uno o más de los siguientes ingredientes: anhídrido carbónico, azúcares, zumos, purés, disgregados de frutas y/o vegetales, extractos vegetales, vitaminas y minerales, aromas, aditivos autorizados u otros ingredientes alimenticios.\r\n', 'SI'),
(13, '20160403', 'Entrevista Santa Teresa', 'La Gastronomía es nuestra pasión, la Tradición nuestro fundamento y \"Somos lo que comemos\", es la filosofía de la compañía Santa Teresa.\r\nAsí, partiendo de materias primas cuidadosamente seleccionadas, sin incorporar ningún tipo de conservante ni aditivo artificial y añadiendo mucha dedicación, investigación y esmero, elaboran una amplia gama de productos gourmet, totalmente naturales y muy saludables.', 'SI'),
(14, '20160401', 'Final de Food Lab 2016', 'Durante el evento, que tuvo lugar en la sala Bertelsmann de Madrid a las 12:00, los responsables de los tres proyectos finalistas respondieron las preguntas que les plantearon los jueces y el público para demostrar por qué su propuesta debía alzarse con el título de \"Startup del sector de alimentación más innovadora de 2016\". El ganador fue Take a chef.', 'SI'),
(15, '20160303', 'Jornada \'Innovar para crecer\'', 'La Jornada \'Innovar para crecer. Ciencia y tecnología para responder al mercado alimentario\', organizada el 3 de marzo por Revista Alimentaria, IMDEA Alimentación, CIAL (UAM-CSIC) y Alibird-CM, reunió en el Salón de Actos de la Fundación IMDEA Alimentación a representantes de centros de investigación, administración, fabricantes y distribución.\r\n', 'SI'),
(16, '20160210', 'Entrevista Alberto Herranz de Interporc', 'Entrevista a Alberto Herranz, Director General de Interporc, en la que nos da a conocer los beneficios para la salud que tiene la carne de cerdo de capa blanca.', 'SI'),
(17, '20160110', 'Entrevista Manuel Lamela', 'Entrevista con Manuel Lamela, presidente de la Fundación Lafer, en la que explica la importancia de la formación de calidad para poder hacer frente a los diversos retos que vive el sector agroalimentario.\r\n', 'SI'),
(18, '20151114', 'Jornada Techsolids sobre gestión de sólidos', 'La Jornada Techsolids sobre Gestión y control de sólidos para la industria alimentaria, organizada por Techsolids (Asociación Española de Tecnologías para Sólidos) y ainia Centro Tecnológico, con la colaboración de Revista Alimentaria, analizó los principales problemas que afectan al sector.\r\n', 'SI'),
(19, '20150930', 'Food Defense: 30 de septiembre', 'Food Defense Soluciones: Hacia la fábrica del futuro, Jornada en Burgos. Durante todo el día, se ha podido aprender qué dicen los referenciales IFS y BRC sobre Food Defense, las claves para implantar con éxito un plan Food Defense. Además se han presentado soluciones tecnológicas y casos de éxito para ofrecer una visión mucho más práctica.', 'SI'),
(20, '20150528', 'I Encuentro \'Éxitos en el mercado internacional\'', 'Cuatrecasas, Gonçalves Pereira y Revista Alimentaria organizaron esta jornada el 28 de mayo de 2015, en la que representantes de varias empresas españolas expusieron sus casos de éxito en el extranjero, en especial en China y Chile', 'SI'),
(21, '20150225', 'Food Defense: 25 de febrero', '\'Food Defense: Hacia la fábrica del futuro\': Jornada gratuita el 25 de febrero en Toledo. Durante el evento se profundizó sobre la necesidad de prevenir una contaminación intencionada de alimentos.', 'SI');
